package controlStatement;

public class C4 {
	//nested if-else//

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=90;
		int y=70;
		int z=70;
	if(x>y) {
	if(x>z) {
			System.out.println(x+"= x ");
		}
		else {
			System.out.println(z+"= Z ");
		}
	}
	else {
	if(y>z)	{
	   System.out.println(y+"= Y ");	
	}
	else {
		System.out.println(z+"= Z");
	}
	}
	
			

	}

}
